# Script para gerar credenciais do Azure para GitHub Actions
# Execute: .\gerar-azure-credentials.ps1

Write-Host "๐ Gerando credenciais do Azure para GitHub Actions..." -ForegroundColor Cyan
Write-Host ""

# Verificar se Azure CLI estรก instalado
try {
    $azVersion = az version --output json 2>&1 | ConvertFrom-Json
    Write-Host "โ Azure CLI encontrado (versรฃo $($azVersion.'azure-cli'))" -ForegroundColor Green
} catch {
    Write-Host "โ Azure CLI nรฃo estรก instalado!" -ForegroundColor Red
    Write-Host "Instale em: https://aka.ms/installazurecliwindows" -ForegroundColor Yellow
    exit 1
}

# Verificar se estรก logado
Write-Host "`n๐ Verificando login no Azure..." -ForegroundColor Cyan
$account = az account show 2>&1
if ($LASTEXITCODE -ne 0) {
    Write-Host "โ๏ธ Nรฃo estรก logado no Azure. Fazendo login..." -ForegroundColor Yellow
    az login
    if ($LASTEXITCODE -ne 0) {
        Write-Host "โ Erro ao fazer login no Azure" -ForegroundColor Red
        exit 1
    }
}

# Obter informaรงรตes da subscription
Write-Host "`n๐ Obtendo informaรงรตes da subscription..." -ForegroundColor Cyan
$subscription = az account show --output json | ConvertFrom-Json
$subscriptionId = $subscription.id
$subscriptionName = $subscription.name

Write-Host "Subscription ID: $subscriptionId" -ForegroundColor Green
Write-Host "Subscription Name: $subscriptionName" -ForegroundColor Green

# Perguntar Resource Group
Write-Host "`n๐ฆ Qual o nome do Resource Group?" -ForegroundColor Cyan
Write-Host "Exemplo: startup-collab-rg" -ForegroundColor Gray
$resourceGroup = Read-Host "Resource Group"

# Verificar se Resource Group existe
$rgExists = az group show --name $resourceGroup --output json 2>&1
if ($LASTEXITCODE -ne 0) {
    Write-Host "โ๏ธ Resource Group '$resourceGroup' nรฃo encontrado." -ForegroundColor Yellow
    Write-Host "Deseja criar? (S/N)" -ForegroundColor Yellow
    $create = Read-Host
    if ($create -eq "S" -or $create -eq "s") {
        Write-Host "Digite a regiรฃo (ex: eastus, westus, brazilsouth):" -ForegroundColor Cyan
        $location = Read-Host "Regiรฃo"
        az group create --name $resourceGroup --location $location
    } else {
        Write-Host "โ Operaรงรฃo cancelada" -ForegroundColor Red
        exit 1
    }
}

# Gerar Service Principal
Write-Host "`n๐ง Criando Service Principal..." -ForegroundColor Cyan
$spName = "github-actions-startup-collab-$(Get-Random -Maximum 9999)"

Write-Host "Nome do Service Principal: $spName" -ForegroundColor Gray

$credentials = az ad sp create-for-rbac `
    --name $spName `
    --role contributor `
    --scopes "/subscriptions/$subscriptionId/resourceGroups/$resourceGroup" `
    --sdk-auth `
    --output json 2>&1

if ($LASTEXITCODE -ne 0) {
    Write-Host "โ Erro ao criar Service Principal:" -ForegroundColor Red
    Write-Host $credentials -ForegroundColor Red
    exit 1
}

# Parse do JSON
try {
    $credentialsJson = $credentials | ConvertFrom-Json
} catch {
    Write-Host "โ Erro ao processar credenciais" -ForegroundColor Red
    Write-Host $credentials -ForegroundColor Red
    exit 1
}

# Mostrar credenciais
Write-Host "`nโ Service Principal criado com sucesso!" -ForegroundColor Green
Write-Host "`n๐ Credenciais geradas:" -ForegroundColor Cyan
